eventScripts.script:
	url = "https://default8237194fde9d4e96b705d713a214c4.ea.environment.api.powerplatform.com/powerautomate/automations/direct/workflows/f063b6c822d9496eb104ac55ae18f05b/triggers/manual/paths/invoke?api-version=1&sp=%2Ftriggers%2Fmanual%2Frun&sv=1.0&sig=ZPsvFboFSg68pNhJe1HM6fV5-ZnKiEL9PSQuRD9vmm8"
#	tagPath = "[default]memTag"
#	val = system.tag.readBlocking(tagPath).toString()
	val = currentValue.value.__str__()
	import java.time

	date = system.date.now()
	timestampUtc = system.date.format(date, "yyyy-MM-dd HH:mm:ss").__str__()
	path = "[default]EnergyWater.value"
	EW = system.tag.readBlocking(path)[0].value
	if EW == 'Energy':
		insight = "Check equipments in microbiology lab"
	else:
		insight = "Check Leakage in washroom"	

	data = {
	    "type": "message",
	    "attachments": [
	        {
	            "contentType": "application/vnd.microsoft.card.adaptive",
    	        "contentUrl": None,
        	    "content": {
	                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    	            "type": "AdaptiveCard",
        	        "version": "1.4",
        	        "body": [
                    	{
	                        "type": "TextBlock",
    	                    "text": "Ignition Notification from Factory Mind",  # You can read this from component props
        	                "weight": "Bolder",
        	                "size": "Large",
        	                "color": "Attention"
        	            },
                    	{
                        "type": "TextBlock",
                        "text": timestampUtc,  # or static text
                        "isSubtle": True,
                        "spacing": "None"
                    	},
                    	{
                        "type": "FactSet",
                        "facts": [
                            {"title": EW,          "value": val},
                            {"title": "Insight", "value": insight}
                        ]
                    	}
                	]
           	 	}
        	}
    	]
	}

	postData = system.util.jsonEncode(data)
	system.net.httpPost(url, "application/json", postData)
