config.script:
	# Get current state
eventState = self.view.params.task1
clickedId = self.props.selectedEvent['eventId']  # Assuming 'event' is the clicked event object
self.view.params.item = self.props.selectedEvent['itemId']
# Update click count and store IDs
if eventState['clickCount'] == 0:
    self.view.params.task1 = {
        "firstId": clickedId,
        "secondId": None,
        "clickCount": 1
    }
elif eventState['clickCount'] == 1:
    self.view.params.task1 = {
        "firstId": eventState['firstId'],
        "secondId": clickedId,
        "clickCount": 2
    }
else:
    # Reset after 2 clicks
    self.view.params.task1 = {
        "firstId": clickedId,
        "secondId": None,
        "clickCount": 1
    }
